crypter = ""def crack(chaine):    for decalage in range(26):        mot = ""        for c in chaine:            if 'A' <= c <= 'Z':                       # majuscule                mot += chr((ord(c) - ord('A') + decalage) % 26 + ord('A'))            elif 'a' <= c <= 'z':                     # minuscule                mot += chr((ord(c) - ord('a') + decalage) % 26 + ord('a'))            else:                                     # autre caractère inchangé                mot += c        print(decalage, "=>", mot)# appelcrack(crypter)